(()=>{"use strict";document.querySelector("#submitBtn").addEventListener("click",(()=>{const e=document.getElementById("title").value,t=document.getElementById("description").value;(function(e,t,n,d){const c=document.querySelector("#content"),o=document.createElement("div");o.classList.add("subdiv"),o.classList.add("card");const i=document.createElement("button");i.textContent="Done",i.classList.add("done-button"),i.addEventListener("click",(function(){c.removeChild(o)})),o.innerHTML="Task: "+e+"<br>Description: "+d+"<br>Priority: "+n+"<br>Due Date: "+t+"<br>",o.appendChild(i);const r=document.createElement("button");r.innerHTML="Delete",r.addEventListener("click",(()=>{o.remove()})),o.appendChild(r),c.appendChild(o)})(e,document.getElementById("due-date").value,document.getElementById("priority").value,t),document.querySelector("form").reset()}))})();